mapper.select:
select 

from wapp_pigowner_game
where gameId = #{gameId,jdbcType=VARCHAR}
  and pigownerId = #{pigownerId,jdbcType=VARCHAR}

mapper.delete:
delete from wapp_pigowner_game
where gameId = #{gameId,jdbcType=VARCHAR}
  and pigownerId = #{pigownerId,jdbcType=VARCHAR}

mapper.update:
update wapp_pigowner_game
set ringnum = #{ringnum,jdbcType=VARCHAR},
  pigowner = #{pigowner,jdbcType=VARCHAR},
  createdDatetime = #{createdDatetime,jdbcType=TIMESTAMP},
  updateDatetime = #{updateDatetime,jdbcType=TIMESTAMP},
  createdBy = #{createdBy,jdbcType=VARCHAR},
  updateBy = #{updateBy,jdbcType=VARCHAR}
where gameId = #{gameId,jdbcType=VARCHAR}
  and pigownerId = #{pigownerId,jdbcType=VARCHAR}
