mapper.select:
		select 
		from nodejs_storage


				and ${customQuerySegment}


              and id = #{queryMap.id,jdbcType=INTEGER }			


              and storageKey = #{queryMap.storageKey,jdbcType=VARCHAR }			


              and storageName = #{queryMap.storageName,jdbcType=VARCHAR }			


              and storageType = #{queryMap.storageType,jdbcType=VARCHAR }			


              and storageSize = #{queryMap.storageSize,jdbcType=INTEGER }			


              and modified = #{queryMap.modified,jdbcType=TIMESTAMP }			


              and url = #{queryMap.url,jdbcType=VARCHAR }			


              and add_time = #{queryMap.add_time,jdbcType=TIMESTAMP }			


              and deleted = #{queryMap.deleted,jdbcType=BIT }			



			order by ${sort}


			limit ${page.offset},${page.pageSize}

		SELECT count(*)
		from nodejs_storage


				and ${customQuerySegment}


              and id = #{queryMap.id,jdbcType=INTEGER }			


              and storageKey = #{queryMap.storageKey,jdbcType=VARCHAR }			


              and storageName = #{queryMap.storageName,jdbcType=VARCHAR }			


              and storageType = #{queryMap.storageType,jdbcType=VARCHAR }			


              and storageSize = #{queryMap.storageSize,jdbcType=INTEGER }			


              and modified = #{queryMap.modified,jdbcType=TIMESTAMP }			


              and url = #{queryMap.url,jdbcType=VARCHAR }			


              and add_time = #{queryMap.add_time,jdbcType=TIMESTAMP }			


              and deleted = #{queryMap.deleted,jdbcType=BIT }

mapper.delete:
		delete from nodejs_storage


				and ${customQuerySegment}


              and id = #{queryMap.id,jdbcType=INTEGER }			


              and storageKey = #{queryMap.storageKey,jdbcType=VARCHAR }			


              and storageName = #{queryMap.storageName,jdbcType=VARCHAR }			


              and storageType = #{queryMap.storageType,jdbcType=VARCHAR }			


              and storageSize = #{queryMap.storageSize,jdbcType=INTEGER }			


              and modified = #{queryMap.modified,jdbcType=TIMESTAMP }			


              and url = #{queryMap.url,jdbcType=VARCHAR }			


              and add_time = #{queryMap.add_time,jdbcType=TIMESTAMP }			


              and deleted = #{queryMap.deleted,jdbcType=BIT }

update.foreach:
			 update nodejs_storage 


                  and storageKey = #{item.storageKey,jdbcType=VARCHAR}			


                  and storageName = #{item.storageName,jdbcType=VARCHAR}			


                  and storageType = #{item.storageType,jdbcType=VARCHAR}			


                  and storageSize = #{item.storageSize,jdbcType=INTEGER}			


                  and modified = #{item.modified,jdbcType=TIMESTAMP}			


                  and url = #{item.url,jdbcType=VARCHAR}			


                  and add_time = #{item.add_time,jdbcType=TIMESTAMP}			


                  and deleted = #{item.deleted,jdbcType=BIT}			


			where
		      id=#{item.id,jdbcType=INTEGER}
